INSERT INTO `t_eat` (`id`, `eatName`, `code`, `viewId`, `eatDesc`, `createDate`, `updateDate`) VALUES ('927cfc9e255d893b02d381e3c74e2d41', '苞芦松', 'AH_HS_C2883b5c4_ABGHSDJFS', 'b79166f5f474aa083f8191835d59f4cd', '西递的“苞芦松”是著名的零食性小吃。用山区的苞芦为山上种植的一种玉米，生长期约为140天，其味清甜，营养成分高。将玉米磨成细粉，经细粉萝刷去皮层，和水搅拌煮成糊状，冷却凝固，再用特制的弓刨成薄片，然后将薄片置于竹匾上晒干即成。食用时，将薄片投入滚油中炸，薄片舒展膨胀上浮，即可捞起。“苞芦松”清香松脆，宜作茶食的佐餐食品。', '2017-3-26 16:44:32', '2017-3-29 09:02:07');
INSERT INTO `t_eat` (`id`, `eatName`, `code`, `viewId`, `eatDesc`, `createDate`, `updateDate`) VALUES ('99ff4afe92a9721b5bd739c84ecdd019', '五城茶干', 'AH_HS_C2883b5c4_CWU90522a', 'b79166f5f474aa083f8191835d59f4cd', '五城茶干以配料讲究、制作精细、色香味美而著称，原料黄豆要当年收的，待五城茶干初步成型后，配以秘制好酱（酱油以本镇洪昌酱园的豆汁原油，或从屯溪程德馨酱园购来），用文火配以桂皮、茴香、丁香、冰糖等调料，以6个时辰慢煮，悉心调色，仔细入味。此时的五城茶干，营养丰富，回味悠长，口感筋道，百吃不厌。', '2017-3-26 16:56:16', '2017-3-29 09:02:18');
INSERT INTO `t_eat` (`id`, `eatName`, `code`, `viewId`, `eatDesc`, `createDate`, `updateDate`) VALUES ('a1a0153d4fb8545a50b5ee704cbdc178', '蟹壳黄烧饼', 'AH_HS_C2883b5c4_QLSV182c9', 'b79166f5f474aa083f8191835d59f4cd', '烧饼形似螃蟹背壳，色如蟹黄，故又名蟹壳黄。它是采用熬炼七八成熟的菜子油炒油酥面，同三分之二的水\r\n面合擀成多层次的面卷，精选梅干菜和五花肉做陷，芝麻撒面，然后放入特制的大炉中，贴于炉壁，烤熟取出。刚出炉的“蟹壳黄”，不待入口，便觉得香味浓烈，咬一块，既酥又脆，层层剥落，满口留香。此饼烤成时呈两面黄色，很似煮熟的螃蟹，放入特制的大炉中，贴于炉壁，烤熟取出，故又名“火炉饼”。因饼形小巧饱满，呈黄色，似蟹壳，故俗称“小烧饼”和“蟹壳黄”。', '2017-3-26 16:50:01', '2017-3-29 09:02:26');
INSERT INTO `t_eat` (`id`, `eatName`, `code`, `viewId`, `eatDesc`, `createDate`, `updateDate`) VALUES ('a21c6ad88da90c55da928be21e7c3e96', '油煎毛豆腐', 'AH_HS_C2883b5c4_HYAJ8da23', 'b79166f5f474aa083f8191835d59f4cd', '毛豆腐是安徽徽州地区特色传统名菜，驰名中外的素食佳肴。由于豆腐通过发酵后使其中植物蛋白转化成多种氨基酸，故经烹饪后味特鲜。系以本省屯溪、休宁一带特产的毛豆腐（它是通过以人工发酵法，使豆腐表面生长出一层白色茸毛。）为主料，用油煎后，佐以葱、姜、糖、盐及肉清汤、酱油等烩烧而成。上桌时以辣椒酱佐食，鲜醇爽口，芳香诱人，并且有开胃作用。', '2017-3-26 16:57:58', '2017-3-29 09:02:33');
INSERT INTO `t_eat` (`id`, `eatName`, `code`, `viewId`, `eatDesc`, `createDate`, `updateDate`) VALUES ('a7da095ab5a5ed9905261a57e0e7c17a', '徽州蒸饺', 'AH_HS_C2883b5c4_WKCe7dcb2', 'b79166f5f474aa083f8191835d59f4cd', '用圆形面皮包做成弦月形饺子，上笼蒸十多分钟即成，用馅原料有鲜肉、豆腐、冬瓜、南瓜等多种，加上香菇、木耳、开洋、火腿丁等配料和葱、姜、蒜、盐、酱油、味精等佐料。', '2017-3-26 16:53:07', '2017-3-29 09:02:41');
INSERT INTO `t_eat` (`id`, `eatName`, `code`, `viewId`, `eatDesc`, `createDate`, `updateDate`) VALUES ('b940da1fc80a16bee7588db6bc191f93', '石头馃', 'AH_HS_C2883b5c4_JN790499e', 'b79166f5f474aa083f8191835d59f4cd', '徽州西递的传统面食，早在清代即负盛名，是有口皆碑的美食。石头馃用五花肉丁和炒黄豆粉扮成馅心，用面皮包上，收口捏紧，用木碾推擀成圆饼形状，然后放在平锅中炕烙，每个饼子上都放置一块石头（仍放一块特制的砖头）压着，放石头的目的是为了均匀的传热，以使馅心熟透，边炕边按动石头（饼子不用翻身），促使内部油脂渗出，一直炕至熟透取出即成。咬开后香气扑鼻，滋润美味，如配汤食用更佳。馃是当地人的俗称，制作中用石头压，故称石头馃，亦称黄豆肉膜。', '2017-3-26 16:42:08', '2017-3-29 09:02:49');
INSERT INTO `t_eat` (`id`, `eatName`, `code`, `viewId`, `eatDesc`, `createDate`, `updateDate`) VALUES ('e2c050c883273d96ed1849cba0589bc2', '炒螺蛳', 'AH_HS_C2883b5c4_ZQ109c1db', 'b79166f5f474aa083f8191835d59f4cd', '徽州的城镇，有很多炒田螺的小吃店，炒得沙沙作响香气扑鼻。这里的田螺个小肉嫩。味道特别鲜，剪去螺屁股，配上作料入锅烧炒，吃时蘸上醋，味道颇佳。假如你坐在新安江畔，或老街的茶楼上，品味着炒田螺，赏玩着清丽的山水，则另有一番滋味。', '2017-3-26 16:54:45', '2017-3-29 09:02:55');
INSERT INTO `t_eat` (`id`, `eatName`, `code`, `viewId`, `eatDesc`, `createDate`, `updateDate`) VALUES ('e3d41232d3e5e647fe78257bb799f844', '腊八粥', 'AH_HS_C2883b5c4_VZdd65c28', 'b79166f5f474aa083f8191835d59f4cd', '农历腊八前将“腊八粥”的佐料准备好，一般以精白的大米为主，另掺以玉米、豆子、山芋、芋艿、瓜干、菜干、干角豆等数十种干鲜蔬果同煮而成，有的雅称“白菜粥”、“百味粥”或“百宝粥”。按旧俗，“腊八粥”煮成后，先作为供品祭祖先，然后才合家食用。', '2017-3-26 16:31:58', '2017-3-29 09:03:11');
INSERT INTO `t_eat` (`id`, `eatName`, `code`, `viewId`, `eatDesc`, `createDate`, `updateDate`) VALUES ('e27be4e5eb53d9db1888b4df18ed5539', '麻酥糖', 'AH_HS_M265dc5d4_405f1a349', '906fdc60a8bfefee20fbf334bb38e02d', '墨酥糖从南宋流传至今为。精品称为“顶市酥”。顶市酥采用脱壳的白芝麻、白糖，配以少量的面粉或米粉，拌以饴糖精制而成。成品白中显黄，抓起成块，提起成带，进嘴甜酥，满口喷香，不粘牙不粘纸，老幼皆宜。徽州习俗，过年时家家都把小红纸包糖装在果子盒里招待客人，到亲戚家去拜年一定要送上墨酥糖。徽州民谚曰：“拜年不带墨酥糖，请君不要进厅堂”。', '2017-3-29 16:30:30', '2017-3-29 17:20:00');
INSERT INTO `t_eat` (`id`, `eatName`, `code`, `viewId`, `eatDesc`, `createDate`, `updateDate`) VALUES ('942dfe42b71c0070c07bd49b137cb003', '红烧桃花鳜', 'AH_HS_M265dc5d4_197f8ae42', '906fdc60a8bfefee20fbf334bb38e02d', '红烧桃花鳜是徽州特产，当地传统名菜之一。桃花鳜原名石级鱼，在黄山桃花盛开时最肥美，故名桃花鳜。这道菜色红诱人，味鲜软嫩，香气扑鼻，是黄山春季时令菜肴。取桃花盛开时节的新鲜石鳜鱼，鱼身两侧打上荷叶清形刀花，用上等酱油浸腌后，放入油中炸得色呈淡黄，配以五花肉丁，春笋丁及葱、姜、盐、料酒、鸡汤，用小火红烧而成。', '2017-3-29 16:44:45', '2017-3-29 17:19:49');
INSERT INTO `t_eat` (`id`, `eatName`, `code`, `viewId`, `eatDesc`, `createDate`, `updateDate`) VALUES ('4a63825f9c6746cf7ca73cc866a3533d', '绿豆兜', 'AH_HS_M265dc5d4_a46bf1144', '906fdc60a8bfefee20fbf334bb38e02d', '绿豆兜是安徽徽州地区特色传统小吃。事先将绿豆催芽，待皮破芽露时，拌小麦粉，加盐、水调和如糊，舀进锅铲上的篾箍内，徐徐潜入沸油锅中，粘结成块后，捞起篾箍，抽去锅铲，炸至暗黄带绿即可。', '2017-3-29 16:51:32', '2017-3-29 17:19:39');
INSERT INTO `t_eat` (`id`, `eatName`, `code`, `viewId`, `eatDesc`, `createDate`, `updateDate`) VALUES ('360a4991e0896598b8444c2591e7e5f3', '手剥笋 ', 'AH_HS_M265dc5d4_ce54a3841', '906fdc60a8bfefee20fbf334bb38e02d', '是利用天目山地带高山野生小笋，经长时间蒸、煮、高温杀菌手剥笋\r\n手剥笋精制而成的，较好地保持了山地野笋的原始口味。又称带壳笋、健脚笋，开袋后味道清鲜纯正，品尝后能立马感受到来自大自然的独特味道，可做为一道精美的冷菜，一般能一口气连吃三四根。上来后，笋都一手半长左右，粗细适中，慢慢剥，从头吃到尾，开头是嫩笋部分，味道怪怪的，有点陈腐的感觉，吃到底根开始脆起来，咬起来发出咯嘣一响，味道也出来了，带鲜，微辣味，感觉还是很清新的。', '2017-3-29 17:03:15', '2017-3-29 17:19:33');
INSERT INTO `t_eat` (`id`, `eatName`, `code`, `viewId`, `eatDesc`, `createDate`, `updateDate`) VALUES ('2adc8ed24ff906e5abf8ab6a0f584c19', '石耳炖土鸡', 'AH_HS_M265dc5d4_4fb080845', '906fdc60a8bfefee20fbf334bb38e02d', '野生石耳是一种药用山菜，在徽菜中属上等名菜，九华山风景区柯村老街一姐妹大排档店里几道菜是用石耳为主品做成的，分别“石耳炖土鸡\"、“石耳炖鸽”、“石耳豆“九华石耳”与“黄山石耳”是一脉相承的，形状和木耳相似，但比木耳大，表面呈黑色，有细刺，背部长着一层青苔似的淡绿膜，正中有蒂，长在悬岩绝壁阴湿处的石隙之中，一般要六七年才能长成。', '2017-3-29 17:06:51', '2017-3-29 17:19:27');
INSERT INTO `t_eat` (`id`, `eatName`, `code`, `viewId`, `eatDesc`, `createDate`, `updateDate`) VALUES ('12fbf7555006cd4fbf47cab8972b0eb4', '臭鳜鱼', 'AH_HS_M265dc5d4GIMb3b0a6', '906fdc60a8bfefee20fbf334bb38e02d', '臭鳜鱼又称臭桂鱼、桶鲜鱼、桶鱼，腌鲜鱼，是一道徽州传统名菜，徽州菜的代表之一，在安徽徽州地区（今安徽省黄山市一带）所谓腌鲜，在徽州本地土话中有臭的意思。这“风味鳜鱼”闻起来臭，吃起来香，肉质鲜嫩、醇滑爽口，保持了鳜鱼的本味原汁。俗名臭鳜鱼。制法独特，食而得异香。', '2017-3-29 17:21:49', '2017-3-29 17:22:06');
INSERT INTO `t_eat` (`id`, `eatName`, `code`, `viewId`, `eatDesc`, `createDate`, `updateDate`) VALUES ('dc5a71469de936c1cd338b62c02e92cd', '腊八豆腐', 'H_HS_M265dc5d4GIM6ce3b4', '906fdc60a8bfefee20fbf334bb38e02d', '腊八豆腐是安徽省黄山市黟县的民间风味特产，也是腊八节的食俗。在腊八前后，黔县家家户户都要晒制豆腐，久而久之，民间将这种自然晒制的豆腐称作“腊八豆腐”。腊八豆腐由大豆、辣椒、五香等原料经传统手工艺精制而成，有“素火腿”之称。成品色泽黄润如玉，入口松软，咸中带甜，又香又鲜。', '2017-3-30 09:55:37', '2017-3-30 09:55:46');
INSERT INTO `t_eat` (`id`, `eatName`, `code`, `viewId`, `eatDesc`, `createDate`, `updateDate`) VALUES ('bb5c2d61997f36f0b91313e25de1f0f4', '三河酥鸭', 'AH_HF_PBC1b005aPBXE6c607', 'c369b7a817f0181f2bfc6732ba2da7f0', '三河酥鸭是安徽合肥肥西县三河镇传统特色名菜。以光鸭为主料，水发香菇为辅料，小葱、姜片、酱油、精盐、味精、八角、桂皮调味。此菜巧控火候，汤清，皮香，肉酥。鸭肉易于消化，能有效抵抗多种炎症；香菇具有高蛋白、低脂肪、多糖、多种氨基酸和多种维生素的营养特点。', '2017-3-30 16:11:13', '2017-3-30 16:11:13');
INSERT INTO `t_eat` (`id`, `eatName`, `code`, `viewId`, `eatDesc`, `createDate`, `updateDate`) VALUES ('ae43daba1c9736700001734f25043ceb', ' 三河虾糊', 'AH_HF_PBC1b005aPBXEcf59e', 'c369b7a817f0181f2bfc6732ba2da7f0', '虾选用小白米虾，洗净去头后备用。大蒜末先下油锅炒出香味后放入虾米翻炒，点上些酱油，倒入白开水继续煮开为止。最后放入用水调好的米粉，(一般来说好的米粉会很干很吃水，要多放点水)，等锅里熬开了就可以再加些葱末、味精、调好味出锅了。刚出锅的虾糊呈暗黄色，亮橙橙的相当诱人。一般吃虾糊得得用勺子，舀上一勺送进嘴里的感觉就是香。因为加了米粉，有米粉的清香，里面有虾，增添了鲜香。总之，这道菜的味道很有特色，风味别具一格，让人一尝难忘。', '2017-3-30 16:19:41', '2017-3-30 16:19:50');
INSERT INTO `t_eat` (`id`, `eatName`, `code`, `viewId`, `eatDesc`, `createDate`, `updateDate`) VALUES ('6713471282fdbde35dc814d20031a599', '三河米饺', 'AH_HF_PBC1b005aPBXE05e3a', 'c369b7a817f0181f2bfc6732ba2da7f0', '三河米饺，又名庐江大弯饺，是安徽省合肥地区特色传统小吃，在安徽省合肥市的肥西县三河镇，庐江县及合肥主城区部分地区均有。以籼米粉制成饺皮，猪五花肉及调料制成馅心，成饺后油炸而成。色泽金黄，外皮微酥脆、馅味鲜美。已有近百年制作历史。这种籼米粉制作的带馅饺子极有名气，人称“三河米饺”。它与太平天国的青年将领陈玉成甚有瓜葛。据传，英王陈玉成大败清军后，受到当地人民的热烈欢迎，纷纷制作点心送给陈玉成的部队，以示慰劳，得到英王部队上下一致赞扬，一直流传至今。因形似饺子，故被称为“三河米饺”。', '2017-3-30 17:18:34', '2017-3-30 17:18:39');
INSERT INTO `t_eat` (`id`, `eatName`, `code`, `viewId`, `eatDesc`, `createDate`, `updateDate`) VALUES ('149ba136a841843e0a968c283f5defa4', '三河马蹄酥', 'AH_HF_PBC1b005aPBXEa708b', 'c369b7a817f0181f2bfc6732ba2da7f0', '马蹄酥选用精白面粉、棉白糖、豆油等原料制作，延用传统洪炉烘制。色呈金黄，香甜酥松，已收入《中国名点菜谱》。', '2017-3-30 17:28:03', '2017-3-30 17:28:08');
