INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('6f757211e5354ce50886e571cf870c2b', '花山谜窟', 'AH_HS_NXE09250a', 'AH_HS', NULL, '古徽州石窟群', '花山谜窟风景区原称“古徽州石窟群”，坐落在安徽省黄山市中心城区（屯溪）篁墩至歙县雄村之间新安江两岸，是以新安江为纽带，连接花山、雄村两大景区，呈哑铃状形态的国家4A级风景名胜区。是黄山山脉的延伸部分，介于北纬29° 44\'~29° 46\'之间。是一个集青山、绿水、田园景致、千年谜窟、奇峰怪石、摩崖石刻、石窟、庙宇、古建筑等自然景观和人文景观之大成，是一个内容丰富、品味高，适宜各层次游客观赏的综合景区。', NULL, 1.00, 0, '2017-3-19 17:37:26', '2017-3-19 17:37:26');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('7f1a68f426eb968de6f2a5b9951f8005', '屯溪老街', 'AH_HS_V[H81069d', 'AH_HS', NULL, '中国历史文化名街', '屯溪老街座落在安徽省黄山市屯溪区中心地段，北面依山，南面傍水，全长1272米，精华部分853米，宽5至8米。包括1条直街、3条横街和18条小巷，由不同年代建成的300余幢徽派建筑构成的整个街巷，呈鱼骨架形分布，西部狭窄、东部较宽。因屯溪老街坐落在横江、率水和新安江三江汇流之处，所以又被称为流动的“清明上河图”，是中国保存最完整、最具有南宋和明清建筑风格的古代街市，也是中国“全国重点文物保护单位”。\r\n2009年，屯溪老街与北京国子监街、苏州平江路一同当选为“中国历史文化名街”。', NULL, 1.00, 0, '2017-3-19 17:37:26', '2017-3-19 17:37:26');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('906fdc60a8bfefee20fbf334bb38e02d', '宏村', 'AH_HF_M265dc5d4', 'AH_HF', NULL, '中国画里的乡村', '宏村位于黟县东北部，建于800多年前的南宋绍熙年间。明代时宏村已百业蒸腾，人丁兴旺，至清代达到了“烟火千家，栋宇鳞次，森然一大都会矣”。位于黟 县北部东山脚下、羊栈河畔的宏村。这是一座形如卧牛的古村落，古人说牛卧马驰是吉兆，牛主富贵，这也许只是一种期盼，但是宏村的居民们却有着实实在在的体验。', NULL, 1.00, 0, '2017-3-19 17:37:26', '2017-3-19 17:37:26');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('b79166f5f474aa083f8191835d59f4cd', '西递', 'AH_HF_C2883b5c4', 'AH_HF', NULL, '桃花源里人家', '西递村始建于北宋，迄今已有950年的历史，为胡姓人家聚居之地。整个村落呈船形，四面环山，两条溪流串村而过，村中街巷沿溪而设，均用青石铺地，整个村落空间自然流畅，动静相宜。西递是一缕淡淡的清风，白墙青檐的深宅，高高耸起的马头墙，青石铺就的石板路，清幽、质朴，足够唤起你对自己前世的猜测：前世一定跟这里有缘，仿佛几百年前的一个黄昏，自己就曾在这高墙围绕的天井里仰望过天空，一阵清风刮过，扬起绣花的裙角……', NULL, 1.00, 0, '2017-3-19 17:37:26', '2017-3-19 17:37:26');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('cdb7dd4dd360d236201425a3c9b99b49', '翡翠谷', 'AH_HS_XW750ca74', 'AH_HS', NULL, '情人谷', '翡翠谷景区在黄山的东部，又称“情人谷”，谷中之水源出炼丹峰、始信峰，游人可至的景区有6000米长，峡谷中分布着大小彩池数百个，有40余个彩池的面积超过100平方米，最大的彩池面积近1000平方米。翡翠谷是由大小几十个水潭组成的。潭水深浅不一，在阳光下泛出青、橙、绿、蓝、黄、白等多种颜色，非常美丽。翡翠谷有100余个潭池，在日光的照耀下呈现明艳的碧色，加之池底岩石纹理相衬，给人一大块翡翠跃然眼前之感。谷中竹林片片，泉声潺潺，岩石之奇瑰，水色之亮丽，令人心旷神怡。', NULL, 1.00, 0, '2017-3-19 17:37:26', '2017-3-19 17:37:26');
