INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('6f757211e5354ce50886e571cf870c2b', '花山谜窟', 'AH_HS_NXE09250a', 'AH_HS', NULL, '古徽州石窟群', '花山谜窟风景区原称“古徽州石窟群”，坐落在安徽省黄山市中心城区（屯溪）篁墩至歙县雄村之间新安江两岸，是以新安江为纽带，连接花山、雄村两大景区，呈哑铃状形态的国家4A级风景名胜区。是黄山山脉的延伸部分，介于北纬29° 44\'~29° 46\'之间。是一个集青山、绿水、田园景致、千年谜窟、奇峰怪石、摩崖石刻、石窟、庙宇、古建筑等自然景观和人文景观之大成，是一个内容丰富、品味高，适宜各层次游客观赏的综合景区。', NULL, 1.00, 0, '2017-3-19 17:37:26', '2017-3-19 17:37:26');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('7f1a68f426eb968de6f2a5b9951f8005', '屯溪老街', 'AH_HS_V[H81069d', 'AH_HS', NULL, '中国历史文化名街', '屯溪老街座落在安徽省黄山市屯溪区中心地段，北面依山，南面傍水，全长1272米，精华部分853米，宽5至8米。包括1条直街、3条横街和18条小巷，由不同年代建成的300余幢徽派建筑构成的整个街巷，呈鱼骨架形分布，西部狭窄、东部较宽。因屯溪老街坐落在横江、率水和新安江三江汇流之处，所以又被称为流动的“清明上河图”，是中国保存最完整、最具有南宋和明清建筑风格的古代街市，也是中国“全国重点文物保护单位”。\r\n2009年，屯溪老街与北京国子监街、苏州平江路一同当选为“中国历史文化名街”。', NULL, 1.00, 0, '2017-3-19 17:37:26', '2017-3-19 17:37:26');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('906fdc60a8bfefee20fbf334bb38e02d', '宏村', 'AH_HS_M265dc5d4', 'AH_HS', NULL, '中国画里的乡村', '宏村位于黟县东北部，建于800多年前的南宋绍熙年间。明代时宏村已百业蒸腾，人丁兴旺，至清代达到了“烟火千家，栋宇鳞次，森然一大都会矣”。位于黟 县北部东山脚下、羊栈河畔的宏村。这是一座形如卧牛的古村落，古人说牛卧马驰是吉兆，牛主富贵，这也许只是一种期盼，但是宏村的居民们却有着实实在在的体验。', NULL, 1.00, 0, '2017-3-19 17:37:26', '2017-3-29 09:01:00');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('b79166f5f474aa083f8191835d59f4cd', '西递', 'AH_HS_C2883b5c4', 'AH_HS', NULL, '桃花源里人家', '西递村始建于北宋，迄今已有950年的历史，为胡姓人家聚居之地。整个村落呈船形，四面环山，两条溪流串村而过，村中街巷沿溪而设，均用青石铺地，整个村落空间自然流畅，动静相宜。西递是一缕淡淡的清风，白墙青檐的深宅，高高耸起的马头墙，青石铺就的石板路，清幽、质朴，足够唤起你对自己前世的猜测：前世一定跟这里有缘，仿佛几百年前的一个黄昏，自己就曾在这高墙围绕的天井里仰望过天空，一阵清风刮过，扬起绣花的裙角……', NULL, 1.00, 0, '2017-3-19 17:37:26', '2017-3-29 09:03:14');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('cdb7dd4dd360d236201425a3c9b99b49', '翡翠谷', 'AH_HS_XW750ca74', 'AH_HS', NULL, '情人谷', '翡翠谷景区在黄山的东部，又称“情人谷”，谷中之水源出炼丹峰、始信峰，游人可至的景区有6000米长，峡谷中分布着大小彩池数百个，有40余个彩池的面积超过100平方米，最大的彩池面积近1000平方米。翡翠谷是由大小几十个水潭组成的。潭水深浅不一，在阳光下泛出青、橙、绿、蓝、黄、白等多种颜色，非常美丽。翡翠谷有100余个潭池，在日光的照耀下呈现明艳的碧色，加之池底岩石纹理相衬，给人一大块翡翠跃然眼前之感。谷中竹林片片，泉声潺潺，岩石之奇瑰，水色之亮丽，令人心旷神怡。', NULL, 1.00, 0, '2017-3-19 17:37:26', '2017-3-19 17:37:26');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('c369b7a817f0181f2bfc6732ba2da7f0', '三河古镇', 'AH_HF_PBC1b005a', 'AH_HF', NULL, '古名鹊渚、鹊尾（渚）、鹊岸', '三河镇上连片的古民居飞檐翘角、雕梁画栋，是皖中地区少见的晚清建筑群。白色的马头墙，小青瓦敷盖的双坡屋顶。梁檩椽柱雕花彩绘，再加黑漆鎏金的店招匾额，悬挂于门楣上的八角玲珑的挂灯，深幽的一人巷长满的青苔，无不透溢浓郁的古风神韵。身临其境，不禁让人发思古之幽情。', NULL, 1.00, 0, '2017-3-29 09:29:20', '2017-3-29 09:29:20');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('ff1b68ac9cda72a1584ebaee4a72f2b9', '吴山镇', 'AH_HF_TX743ef35', 'AH_HF', NULL, '吴王故里、铁字之源、贡鹅之乡', '长丰县吴山镇位于安徽省会合肥的北郊，距省会合肥市区28公里，属合肥市卫星城镇，北距安徽煤城淮南市60公里，全省200家中心镇之一、安徽省首批千年古镇，全国重点镇。旅游资源有吴王古墓、公主墓、吴王庙、百花园遗址、杨渥古街、贞节牌坊、吴山庙武装起义纪念碑、纪念馆、叶挺将军旧居、魏老河生态旅游乐园、吴山贡鹅、吴山铁字、矿泉水等，其中“吴王遗踪”景点被评为合肥“十景”之一。', NULL, 1.00, 0, '2017-3-29 09:47:34', '2017-3-29 09:47:34');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('9b10464d48a98b8d05e97504cbb64071', '撮镇', 'AH_HF_Qd1bc3294', 'AH_HF', NULL, '因孔子周游列国时喟然叹曰“地多一撮，书重百城”之典故而得名', '撮镇隶属安徽省合肥市肥东县，位于合肥市东郊，南濒巢湖，北连肥东县城店埠镇，西接瑶海区大兴镇，镇域总面积116.7平方公里，人口10.8万人（2015年），辖10个村，10个社居委，3个工业区。是肥东县南部地区的政治、经济、文化、商贸和交通中心。主要景点有渡江战役总前委旧址瑶岗纪念馆、曹公桥、龙栖地生态农业旅游观光园等。', NULL, 1.00, 0, '2017-3-29 09:55:07', '2017-3-29 10:07:54');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('c69300177316cf9bee0d36db5956d2fd', '长临河镇', 'AH_HF_NGB60e1f0', 'AH_HF', NULL, '南濒巢湖，亦作长宁河', '位于肥东县境南部，西南滨巢湖，东南和巢湖市居巢区接壤，东北与撮镇镇、桥头集镇交界，西靠南淝河，与合肥市包河区隔河相望。距县城22公里。长临河镇寺门口村有座长宁寺，建于三国东吴赤乌年间，青阳山之水从寺前流至 龙庵注入巢湖，久之形成河，河名长宁河，因此地滨临巢湖，后更名为长临河。镇名源于长临河地名。', NULL, 1.00, 0, '2017-3-29 10:02:30', '2017-3-29 10:07:36');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('cc3e5d519d525de2399ce48d49365e59', '汤池镇', 'AH_HF_LHaf64e14', 'AH_HF', NULL, '九福之地，温泉古镇，名茶之乡', '汤池古镇，1996年被列为安徽省重点小城镇建设试点镇，1998年省政府批准汤池为省级风景名胜区，2003年汤池镇被巢湖市批准为“温泉旅游度假区”。 2004年被评为安徽省江淮十大名镇之一，2005年被评为国家AAAA级风景名胜区，2006年被评为安徽省最佳旅游乡镇，2007年被国家环保总局评为安徽省环境优美乡镇。', NULL, 1.00, 0, '2017-3-29 10:13:49', '2017-3-29 10:13:49');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('abae2805bdf696b5d5ed84e161351707', '柘皋镇', 'AH_CH_HQcbb424d', 'AH_CH', NULL, '古称橐皋，西周初期群舒一支在此建立宗国', '柘皋名胜古迹众多，柘皋八景斐声方圆数百里，其中“峏山白龙洞”有着神话般的传说。据说很久以前有一条白蛇在峏山主峰─龙洞内修炼。久之，白蛇得道变成白龙飞天后留此白龙洞，洞口宽约10平方米，洞深数里，其间布满石乳、石笋，且闻泉水叮咚响。“玉栏古桥”更有其不同寻常的历史渊源。相传元末朱元璋起义，遇元兵追杀，至柘皋河边，进退维谷，危难之际，一老丈从苇丛中推出一只小船，轻点竹篙，将朱元璋渡过河去。后明朝立，朱元璋在柘皋河上修一石桥，取名“遇难桥”，以报老丈当日救命之恩。后人见“遇难”二字不祥，遂改为谐音“玉栏桥”，相传至今。', NULL, 1.00, 0, '2017-3-29 10:22:26', '2017-3-29 10:22:26');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('6f766bae0820ec7cd2763604ed97eeae', '查济古村', 'AH_XC_UTCebe725', 'AH_XC', NULL, '十里查村九里烟，三溪汇流万户间', '同样是小桥流水人家，江南的那些古镇却早已被众多客人蹂躏踩踏，而查济，一座散发着古朴典雅气息的村落，其破败颓废的沧桑之美，或许会成为你一直在寻找的归隐之地。“青山横西郭，泉水绕查村。匆匆金陵客，淳朴山乡人。白云游子意，落日故国情。挥手自兹去，复蹈车马尘。”这首著名画家陈丹青写的旧体诗，正是查济神貌的独特表达，更把当代人的心境展露无遗。', NULL, 1.00, 0, '2017-3-29 10:32:31', '2017-3-29 10:34:40');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('67aff1375a24217f359735a37d05026f', '关麓古村', 'AH_HS_HGC441001', 'AH_HS', NULL, '中国古代私塾文化博物馆', '关麓村地处武亭山麓的西武岭脚下，因西武岭有“西武雄关”之称，而该村居雄关之东麓，因此得名。关麓村始建于五代、后唐时期，距今已有上千年的历史，是以汪姓为主聚居的村落。关麓仅是个百余户人的小村落，但历来崇尚读书，文化气息浓厚，特别是那设计独特、冷峻恢宏的八座联体大宅，为中国所罕见，是个值得造访的地方。', NULL, 1.00, 0, '2017-3-29 10:39:38', '2017-3-29 10:39:38');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('820c71801c909d612892e3ee9978742d', '孔城镇', 'AH_AQ_QW9e5212d', 'AH_AQ', NULL, '安徽第一古街', '孔城，历史悠久，人文资源丰富。宋《元丰九域志》载：“淮南路舒州九镇，孔城即九镇之一”。镇区内孔城老街，全长2公里，临街铺面保存完好，是华东地区保存最为完整、体量最大、原汁原味的一条老街。著名的桐乡书院、朝阳楼坐落其中，沿街建筑风格古朴苍劲，木制阁楼、青石板街面更是江北奇观。', NULL, 1.00, 0, '2017-3-29 10:43:00', '2017-3-29 10:43:00');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('f0f0266836d51e57ac4aa0401a493043', '呈坎古镇', 'AH_HS_CUC4139b9', 'AH_HS', NULL, '呈坎双贤里、江南第一村', '呈坎原名龙溪，后改名为呈坎。盖地仰曰“呈”，洼下曰“坎”，整个村落按《易经》、“阴（坎），阳（呈），二气统一，天人合一”的八卦风水理论选址布局，依山傍水，形成三街·九十九巷、宛如迷宫。在村落建设上，呈坎八卦村按先天八卦图主四卦布局形成，。同时古老的龙溪河宛如玉带，呈“S”形穿村而过，形成阴阳鱼的分界线；村落周边大环境矗立着八座大山，自然形成了八卦的八个方位，共同构成了天然八卦布局。人文八卦与天然八卦融合的巧妙布局，使呈坎八卦村成为中国古村落建设史上的一大奇迹。', NULL, 1.00, 0, '2017-3-29 10:46:50', '2017-3-29 10:46:50');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('adf2cd562d27e0f3591ac82ab7ecd237', '南屏', 'AH_HS_Nb1b00fd4', 'AH_HS', NULL, '中国影视村', '南屏村位于安徽省黟县西南，是一座有千年历史、规模宏大的古村落，因《菊豆》、《卧虎藏龙》等多部著名影片在此拍摄，被称为中国影视村。全村有近三百多座明清古建筑，村中有36眼井，72条巷，从村头到村尾二百多米的一条中轴线上，至今保留这八个大小祠堂，这种祠堂群在全省也是仅此一家。南屏最具特色的建筑应属祠堂群。南屏村大姓叶氏宗祠“序秩堂”就是一座占地2000平方米，歇山重檐、端庄轩敞的古祠。祠堂门前照壁矗立，门楼高大，一对一人多高的大石鼓，雕刻十分精致。4根大石柱托着额枋，上面雕刻着古鼎宝瓶类的祭器。序秩堂分上、中、下三厅。', NULL, 1.00, 0, '2017-3-29 10:52:43', '2017-3-29 10:52:43');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('f0ade9c89b888ca1c1e6e6656f47cf12', '万涧古村', 'AH_AQ_NTC4e8161', 'AH_AQ', NULL, '竹、木资源丰富', '龙潭乡万涧村紧靠国家级风景名胜区“天柱山”的北部，东与杜埠村接壤，南与天柱山相依，西与“天柱林场”为邻，北与龙潭村毗连。天柱山后山主景区景点全部座落在万涧境内，青龙背、天狮峰、回音台、大天门、叠翠亭、蜒蚰石、千丈崖、观音洞、花戏楼、杨老屋古民居、关门井及万亩竹海、百丈瀑布等自然景点，均有很高的观赏价值。', NULL, 1.00, 0, '2017-3-29 10:58:08', '2017-3-29 10:59:48');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('4a54499597f3f213377a595cae7772fc', '棠樾古村', 'AH_HS_TCCdb9b50', 'AH_HS', NULL, '慈孝天下无双里，锦绣江南第一乡', '棠樾古村位于安徽省黄山市歙县，以牌坊群而闻名于世，它是一处以鲍氏家族的“孝梯”为核心的聚居所住。在漫长的封建社会里，这里曾经出现了众多的忠臣、孝子、节妇，涌现出许多感人肺腑的故事,这样的“忠孝”被以树牌坊，旌表德行，承沐后恩，流芳百世,这种封建礼教的儒家伦理道德，成为了人们一生的最高追求。棠樾牌坊群是徽州的代表作，也是古徽州绝作之一,每座牌坊，都有一个动人的故事，对于历史，对于建筑艺术，对于徽商，对于探寻其中的美丽故事，是有着无限的感慨和感受的。', NULL, 1.00, 0, '2017-3-29 11:00:57', '2017-3-29 11:00:57');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('c6f32b616224070c49b8abca67dfa56a', '临涣镇', 'AH_HB_GPfb4afe8', 'AH_HB', NULL, '古称铚，又名古茶镇', '地处淮北的临涣镇也是品茶的好去处。斗大的“茶” 字招徕着人们的无限茶欲，饮茶是他们每天生活的一部分。 这个1万多人口的安徽淮北小镇经历了3000年的历史风雨侵蚀，而茶文化则自明代起便一代代“香烟”不断，延续了600多年。那里茶香独特，是淮北地区颇有名气的古茶镇。', NULL, 1.00, 0, '2017-3-29 11:03:44', '2017-3-29 11:03:44');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('6525654eebfa4edd5e3073efc8f4aa90', '塔川', 'AH_HS_Ee129ea34', 'AH_HS', NULL, '又名塔上，隶属于黟县宏村镇', '塔川是中国四大秋色之一，它的秋色不仅在于自然风光，穿过村口的古木林，沿溪流进入村中，就见二三十栋粉墙黛瓦的古民居依山而建，层层叠叠，错落有致。最美的是塔川清晨，薄薄的雾气笼罩在树上，似有似无。农家小屋若隐若现，红色枫叶中露出飞檐翘角。粉墙黛瓦在五彩叶子的掩映下，如中国山水画般诗情画意。', NULL, 1.00, 0, '2017-3-29 11:08:48', '2017-3-29 11:08:48');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('51777407285db3607992b60da432734e', '渔梁', 'AH_HS_Cb00c32a4', 'AH_HS', NULL, '江南都江堰，渔梁坝', '渔梁镇位于安徽省歙县，由一座座民宅依练江而建，镇里有条渔梁古街，依山傍水，蜿蜒而去。宋代大儒朱熹讲学的紫阳山上竹涛阵阵；山光云影一起在碧波间荡漾…除此之外，渔梁古镇还拥有可与都江堰媲美的渔梁坝，历经百年风雨，沧桑尽显，站在坝上远眺，练江美景尽收眼底。老街的气质就在这宁静的氛围中沉淀。', NULL, 1.00, 0, '2017-3-29 11:11:38', '2017-3-29 11:11:38');
INSERT INTO `t_view` (`id`, `viewName`, `code`, `cityCode`, `viewKewWords`, `viewSummary`, `viewDesc`, `viewSeason`, `rebate`, `visitNum`, `createDate`, `updateDate`) VALUES ('d7b9d044a3597a948941772169c6b2d6', '阳产', 'AH_HS_E09c47734', 'AH_HS', NULL, '各家土楼上争奇斗艳式晒秋', '阳产是安徽省歙县的一个小山寨，这里有皖南最大的土楼群，土黄泛红的墙，乌黑的房瓦，掩映在黄绿相间的杉木之中，层层叠叠甚是震撼。每当秋天来临，村里三百多栋土楼的院子都争奇斗艳式的晒秋，玉米、辣椒、柿子、黄豆……这里的秋味是人与自然的完美结合，散发着中国古老传统的秋的味道。', NULL, 1.00, 0, '2017-3-29 11:13:49', '2017-3-29 11:13:49');
